import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/form.css';

const Form = () => {
  const [adults, setAdults] = useState(0);
  const [children, setChildren] = useState(0);
  const [adultAges, setAdultAges] = useState([]);
  const [childAges, setChildAges] = useState([]);
  const [premium, setPremium] = useState(null);
  const [isAdultAgeInvalid, setIsAdultAgeInvalid] = useState(false);
  const [isChildAgeInvalid, setIsChildAgeInvalid] = useState(false);

  const navigate = useNavigate();

  const handleAddAdult = () => {
    if (adults < 2) {
      setAdults(adults + 1);
      setAdultAges([...adultAges, '']);
    }
  };

  const handleRemoveAdult = () => {
    if (adults > 1) {
      setAdults(adults - 1);
      setAdultAges(adultAges.slice(0, -1));
    }
  };

  const handleAddChild = () => {
    if (children < 4 && adults > 0) {
      setChildren(children + 1);
      setChildAges([...childAges, '']);
    }
  };

  const handleRemoveChild = () => {
    if (children > 0) {
      setChildren(children - 1);
      setChildAges(childAges.slice(0, -1));
    }
  };

  const handleAdultAgeChange = (index, value) => {
    const parsedValue = parseInt(value, 10);
    const isValid = !isNaN(parsedValue) && parsedValue >= 18 && parsedValue <= 90;

    setIsAdultAgeInvalid(!isValid);

    const updatedAges = [...adultAges];
    updatedAges[index] = isValid ? parsedValue : '';
    setAdultAges(updatedAges);
  };

  const handleChildAgeChange = (index, value) => {
    const parsedValue = parseInt(value, 10);
    const isValid = !isNaN(parsedValue) && parsedValue >= 0 && parsedValue <= 17;

    setIsChildAgeInvalid(!isValid);

    const updatedAges = [...childAges];
    updatedAges[index] = isValid ? parsedValue : '';
    setChildAges(updatedAges);
  };

  const handleSubmit = () => {
    // Check if at least 1 adult is selected
    if (adults === 0) {
      alert('Select at least 1 adult to calculate premium.');
      return;
    }

    // Prepare data to send to the backend
    const ages = [...adultAges, ...childAges];

    const data = {
      ages: ages,
      city: '', // Empty value for city
      cover: '', // Empty value for cover
      tenure: '', // Empty value for tenure
    };

    // Send data to the backend for premium calculation
    fetch('http://localhost:3001/api/calculate_premium', {
      method: 'POST',
      mode: "cors",
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the received premium data
        setPremium(data.premium);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleAddToCart = () => {
    // Navigate to the payment page with the premium amount as a query parameter
    navigate(`/paymentpage?amount=${premium}`);
  };

  return (
    <div>
      <h1>Health Insurance Premium Calculator</h1>
      <div>
        <label>Adults:</label>
        <button onClick={handleAddAdult}>+</button>
        <button onClick={handleRemoveAdult}>-</button>
        <br />
        {adultAges.map((age, index) => (
          <input
          key={index}
          type="number"
          value={age}
          onChange={(e) => handleAdultAgeChange(index, e.target.value)}
          min="18"
          max="90"
          className={isAdultAgeInvalid ? 'invalid' : ''}
        />
        ))}
      </div>
      <div>
        <label>Children:</label>
        <button onClick={handleAddChild} disabled={adults === 0}>
          +
        </button>
        <button onClick={handleRemoveChild}>-</button>
        <br />
        {childAges.map((age, index) => (
          <input
          key={index}
          type="number"
          value={age}
          onChange={(e) => handleChildAgeChange(index, e.target.value)}
          min="0"
          max="17"
          className={isChildAgeInvalid ? 'invalid' : ''}
        />
        ))}
      </div>
      <div>
        <label>City:</label>
        <select required>
          <option value="">Select City</option>
          <option value="1">Tier 1</option>
          <option value="2">Tier 2</option>
        </select>
      </div>
      <div>
        <label>Cover:</label>
        <select required>
          <option value="">Select Cover</option>
          <option value="300000">3,00,000</option>
          <option value="400000">4,00,000</option>
          <option value="500000">5,00,000</option>
        </select>
      </div>
      <div>
        <label>Tenure:</label>
        <select required>
          <option value="">Select Tenure</option>
          <option value="1">1 year</option>
          <option value="2">2 years</option>
          <option value="3">3 years</option>
        </select>
      </div>
      <button onClick={handleSubmit}>Calculate Premium</button>
      {premium !== null && (
        <div>
          <p>Premium: {premium}</p>
          <button onClick={handleAddToCart}>Add to Cart</button>
        </div>
      )}
    </div>
  );
};

export default Form;
